CREATE TABLE `users` (`id` INT(11) NOT NULL AUTO_INCREMENT , `firstname` VARCHAR(255) NOT NULL , `lastname` VARCHAR(255) NOT NULL , `login` VARCHAR(255) NOT NULL , `password` VARCHAR(255) NOT NULL , `token` VARCHAR(255) NOT NULL , `support` BIT(1) NOT NULL , `admin` BIT(1) NOT NULL , `root` BIT(1) NOT NULL , PRIMARY KEY (`id`))
CREATE TABLE `tickets` (`id` INT(11) NOT NULL AUTO_INCREMENT , `title` VARCHAR(255) NOT NULL , `problem` TEXT NOT NULL , `datetime` DATETIME NOT NULL , `created_by` INT(11) NOT NULL , `worked_by` INT(11) NOT NULL , `solved` BIT(1) NOT NULL , PRIMARY KEY (`id`))
CREATE TABLE `messages` (`id` INT(11) NOT NULL AUTO_INCREMENT , `user` INT(11) NOT NULL , `ticket_id` INT(11) NOT NULL , `datetime` DATETIME NOT NULL , `message` TEXT NOT NULL , PRIMARY KEY (`id`))